    const [text,setText] = useState('')
  return (
    <div className="bckgrnd">
      <Form>
        <Form.Group>
          <InputGroup>
            <Form.Control as ="textarea" required value ={text} onChange={e =>setText(e.target.value)} style ={{height : '75px', resize : 'n'}}></Form.Control>
          </InputGroup>
        </Form.Group>
      </Form>
    </div>
  );
};

export default MainConversation;
