import React, { useContext, useState } from "react";
import useLocalStorage from "../UseLocalStorage";
import { useContacts } from "./ContactsProvider";

const ConversationsContext = React.createContext();

export function useConversations() {
  return useContext(ConversationsContext);
}

export function ConversationsProvider({ children }) {
  
  const [conversations, setConversation] = useLocalStorage("conversations", []);
  const [selectedConversationIndex, setSelectedConversationIndex] = useState(0);
  const { contacts } = useContacts();
  function createConversation(recipients) {
    setConversation((prevConversations) => {
      return [...prevConversations, { recipients, messages: [] }];
    });
  }
  function addMessageToConversation({ recipients, text, sender }) {
    setConversation(prevConversations=>{let madeChange = false 
      const newMessage= {sender,text} 
      const newConversations = prevConversations.map
      (conversation=>{
        if(arrayEquality(conversation.recipients,recipients))
        {
        madeChange = true
        return {...conversation,messages:[conversation.messages,newMessage]
      }}
      return conversation})
    if(madeChange){return newConversations}else {return[...prevConversations,{recipients,messages :[newMessage]}
  ]}})
  }
  function sendMessage( recipients, text) {
    addMessageToConversation({recipients,text, sender : Email} )
  }
  const formattedConversations = conversations.map((conversation, index) => {
    const recipients = conversation.recipients.map((recipient) => {
      const contact = contacts.find((contact) => {
        return contact.Email === recipient;
      });
      const name = (contact && contact.Name) || recipient;
      return { Email: recipient, name };
    });
    const selected = index === selectedConversationIndex;
    return { ...conversation, recipients, selected };
  });
  const value = {
    conversations: formattedConversations,
    selectedConversation: formattedConversations[selectedConversationIndex],
    sendMessage,
    selectConversationIndex: setSelectedConversationIndex,
    createConversation,
  };
  return (
    <ConversationsContext.Provider value={value}>
      {children}
    </ConversationsContext.Provider>
  );
}
function arrayEquality(a,b){
  if(a.length !==b.length) return false
  a.sort()
  b.sort()
  return a.every((element,index)=>{return element=== b[index]})
}
