import React, { useState } from "react";
import "./SideBar.css";
import { Form, InputGroup } from "react-bootstrap";
import { Button } from "reactstrap";
import { useConversations } from "./Context/ConversationsProvider";

const MainConversation = () => {
  const [text, setText] = useState("");
  const { sendMessage, selectedConversation } = useConversations();
  function handleSubmit(e) {
    e.preventDefault();
    sendMessage(
      selectedConversation.recipients.map((r) => r.Email),
      text
    );
    setText("");
  }
  return (
    <div className="bckgrnd ">
      <div className="flex-grow-1 overflow-auto"></div>
      <div className="d-flex flex-column align-item-start justify-content-end px-3">
        
          {selectedConversation.messages.map((message,index)=>{
            return(
              <div key={index} className ={`my-1 d-flex flex-column ${message.fromMe? 'align-self-end' :''}`}>
              <div className={`rounded px-2 py-1 ${message.fromMe?`bg-primary text-white` : `border`}`}>{message.text}</div>
              <div className = {`text-muted small ${message.fromMe? 'text-right' : ''}`}>{message.fromMe? 'You': message.senderName}</div></div>
            )
            })}
       
      </div>
      <div>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="m-1">
            <InputGroup>
              <Form.Control
                as="textarea"
                required
                value={text}
                onChange={(e) => setText(e.target.value)}
                style={{ height: "38px", resize: "none" }}
              ></Form.Control>
              <span class="input-group-append height:100% ">
                <Button color="secondary" type="submit">
                  Send
                </Button>
              </span>
            </InputGroup>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
};
export default MainConversation;
