import React, { createContext, useRef,useState } from "react";
import { Modal, Form } from "react-bootstrap";
import {Button } from "reactstrap";
import Axios from "axios";
import "react-toastify/dist/ReactToastify.css";
import { toast } from "react-toastify";

const NewContactModal = ({ closeModal }) => {
  const EmailRef = useRef();
  const NameRef = useRef();
  const [Email, setEmail] = useState("");
  const [Name, setName] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    Axios.post("http://localhost:3001/NewContact", {
      Email: Email,
      Name: Name,
    }).then((response) => {
      if (response.data.message) {
        toast.error("Cannot find user", {
          position: toast.POSITION.BOTTOM_CENTER,
          autoClose: false,
        });
      } else {
           // createContact(EmailRef.current.value,nameRef.current.value)
           closeModal();

      }
    });
    
  }
  return (
    <>
      <Modal.Header closeButton>Create Contact</Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="text" ref={EmailRef} required  onChange={(e) => {
            setEmail(e.target.value);
          }}/>
          </Form.Group>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" ref={NameRef} required onChange={(e) => {
            setName(e.target.value);
          }} />
          </Form.Group>
          <Button outline color="secondary" className="mt-2">Create</Button>
        </Form>
      </Modal.Body>
    </>
  );
};

export default NewContactModal;
